import java.util.Scanner;

public class adding_diagonals {
    public static void main(string[] args) {
        Scanner abc=new Scanner(System.in);
        int a = abc.nextInt();
        int b= abc.nextInt();
        int[][] arr=new int[a][b];
        int sum=0;
        for(int i=0;i<a;i++){
            for(int j=0;j<b;j++){
                arr[i][j]=abc.nextInt();
            }
        }for(int i=0;i<a;i++){
            for(int j=0;j<b;j++){
                if(i==j || i+j==a-1){                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       sum+=arr[i][j];
                }
            }
        }System.out.println(sum);
    }
}
